func countingSort(arr []int) []int {
	min_value := arr[0]
	max_value := arr[0]
	for _, val := range arr {
		min_value = min(min_value, val)
		max_value = max(max_value, val)
	}
	size := max_value - min_value + 1
	count := make([]int, size)
	for _, val := range arr {
		count[val - min_value]++
	}
	arr_sorted := make([]int, 0, len(arr))
	index := 0
	for i := 0; i < size; i++ {
		for j := 0; j < count[i]; j++ {
			arr_sorted = append(arr_sorted, i + min_value)
		}
	}
	return arr_sorted
	}

