function merge(arr, left, mid, right) {
	let n1 = mid - left + 1;
	let n2 = right - mid;

	let leftArr = new Array(n1);
	let rightArr = new Array(n2);

	for (let i = 0; i < n1; i++) {
		leftArr[i] = arr[left + i];
	}
	for (let j = 0; j < n2; j++) {
		rightArr[j] = arr[mid + 1 + j];
	}

	let i = 0;
	let j = 0;
	let k = left;

	while (i < n1 && j < n2) {
		if (leftArr[i] <= rightArr[j]) {
			arr[k] = leftArr[i];
			i++;
		} else {
			arr[k] = rightArr[j];
			j++;
		}
		k++;
	}

	while (i < n1) {
		arr[k] = leftArr[i];
			i++;
		k++;
	}

	while (j < n2) {
		arr[k] = rightArr[j];
			j++;
		k++;
	}
}

