	while (gap > 0) {
		// Do an insertion sort for each sub-array with size gap
		for (int i = gap; i < n; i++) {
		// The first element (arr[i]) is already in its place
		int temp = arr[i];
		int j = i;
		while (j >= gap && arr[j - gap] > temp) {
			arr[j] = arr[j - gap];
			j -= gap;
		}
		arr[j] = temp;
		}
		gap /= 2;
	}

